import React, { Component } from 'react';
import { MDBBtn } from 'mdbreact';
import { MDBListGroup, MDBListGroupItem, MDBContainer } from "mdbreact";
import { BrowserRouter, Route, Link, Redirect } from 'react-router-dom';
import Nav_bar from './Nav_bar/nav_bar';


class Cards extends Component {
    constructor(props) {
        console.log(localStorage.getItem('access_token'))
        super(props);
        this.state = {
            id: null,
            user_id: null,
            last_4: null,
            brand: null,
            expired_at: null,
            btnCreate: false,
            btnDisplay: false,
            checkConnection: false,
            listCards: [],
            index: 2
        };
        this.handleBtnCreate = this.handleBtnCreate.bind(this);
        this.handleBtnUpdate = this.handleBtnUpdate.bind(this);
        this.handleBtnDisplay = this.handleBtnDisplay.bind(this);
        this.handleBtnDisplayAll = this.handleBtnDisplayAll.bind(this);
        this.handleBtnDelete = this.handleBtnDelete.bind(this);
        this.handleBtnCancel = this.handleBtnCancel.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleChangePwd = this.handleChangePwd.bind(this);
        this.handleChangeExpired = this.handleChangeExpired.bind(this);
        this.handleChangeId = this.handleChangeId.bind(this);

    }

    

    handleChange(event) {
        this.setState({
            brand: event.target.value
        });
    }

    handleChangePwd(event) {
        this.setState({
            last_4: event.target.value
        });
    }

    handleChangeExpired(event) {
        this.setState({
            expired_at: event.target.value
        });
    }

    handleChangeId(event) {
        this.setState({
            id: event.target.value
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        let result = fetch("http://localhost:8000/v1/cards", {
            method: 'POST',
            body: JSON.stringify({
                "last_4": this.state.last_4,
                "brand": this.state.brand,
                "expired_at": this.state.expired_at
            }),
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': localStorage.getItem('access_token')
            }
        })
            .then(response => {
                response.json()
                    .then(value => {
                        if (response.status === 200) {
                            let access_token = response.access_token;
                            console.log(access_token);
                            this.setState({
                                checkConnection: true,
                            })
                        }
                    });
            });
    }
    handleSubmitUpdate = (e) => {
        e.preventDefault();
        let url= "http://localhost:8000/v1/cards/"+ this.state.id
        let result = fetch(url, {
            method: 'PUT',
            body: JSON.stringify({
                "last_4": this.state.last_4,
                "brand": this.state.brand,
                "expired_at": this.state.expired_at
            }),
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': localStorage.getItem('access_token')
            }
        })
            .then(response => {
                response.json()
                    .then(value => {
                        if (response.status === 200) {
                            let access_token = response.access_token;
                            console.log(access_token);
                            this.setState({
                                checkConnection: true,
                            })
                        }
                    });
            });
    }

    handleSubmitDelete = (e) => {
        e.preventDefault();
        let url = "http://localhost:8000/v1/cards/" + this.state.id
        let result = fetch(url, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': localStorage.getItem('access_token')
            }
        })
            .then(response => {

            });
    }


    handleBtnCreate() {
        this.setState({
            btnCreate: true
        })
    }
    handleBtnUpdate() {
        this.setState({
            btnUpdate: true
        })
    }

    handleBtnDisplay = (e) => {

        this.setState({
            btnDisplay: true
        })
    }
    handleBtnDisplayAll = (e) => {

        this.setState({
            btnDisplay: true
        })
    }

    handleBtnCancel = (e) => {
        console.log("dans cancel")
        this.setState({
            checkConnection: true,

        })
    }
    handleBtnDelete = (e) => {

        this.setState({
            btnDelete: true
        })
    }

    handleSubmit2 = (e) => {
        e.preventDefault();
        console.log("okkkkkkkkkkkk")
        let result = fetch("http://localhost:8000/v1/cards", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': localStorage.getItem('access_token')
            }
        })
            .then(response => {
                response.json()
                    .then(value => {

                        if (response.status === 200) {
                            this.setState({
                                //   checkConnection: true,
                                id: value[0].id,
                                last_4: value[0].last_4,
                                brand: value[0].brand,
                                expired_at: value[0].expired_at

                            })
                        }
                    });
            });
    }


    render() {
        return (

            <div>

                <div>
                    <Nav_bar />
                    <MDBBtn onClick={this.handleBtnCreate}>Create card</MDBBtn>
                    <MDBBtn onClick={this.handleBtnUpdate}>Update card</MDBBtn>

                    <MDBBtn onClick={this.handleBtnDisplay}>Display cards</MDBBtn>
                    <MDBBtn onClick={this.handleBtnDisplayAll}>Display all cards</MDBBtn>
                    <MDBBtn onClick={this.handleBtnDelete}>Delete cards</MDBBtn>
                    <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
                    {this.state.btnCreate ?
                        <div>

                            <form onSubmit={this.handleSubmit}><div class="form-group col-md-4">
                                <label for="last_4">last_4 :</label>
                                <input type="text" id="last_4" name="last_4" onChange={this.handleChangePwd} class="form-control"></input>
                                <label for="expired_at">expired_at:</label>
                                
                                <input type="text" id="expired_at" name="expired_at" onChange={this.handleChangeExpired} class="form-control"></input>

                                <label for="inputState">Brand</label>
                                <select id="brand" name="brand" onChange={this.handleChange} class="form-control">
                                    <option brand>Choose...</option>
                                    <option>visa</option>
                                    <option>master_card</option>
                                    <option>american_express</option>
                                    <option>union_pay</option>
                                    <option>jcb</option>
                                </select>

                                <MDBBtn type="submit" >Send</MDBBtn>
                                <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn></div>

                            </form>
                        </div>
                        :
                        <div>
                            {this.state.btnUpdate ?
                                <div>
                                   <form onSubmit={this.handleSubmitUpdate}><div class="form-group col-md-4">
                                   <label for="last_4">Enter id of the card to update :</label>
                                <input type="text" id="id" name="last_4" onChange={this.handleChangeId} class="form-control"></input>
                                
                                <label for="last_4">last_4 :</label>
                                <input type="text" id="last_4" name="last_4" onChange={this.handleChangePwd} class="form-control"></input>
                                <label for="expired_at">expired_at:</label>
                                
                                <input type="text" id="expired_at" name="expired_at" onChange={this.handleChangeExpired} class="form-control"></input>

                                <label for="inputState">Brand</label>
                                <select id="brand" name="brand" onChange={this.handleChange} class="form-control">
                                    <option brand>Choose...</option>
                                    <option>visa</option>
                                    <option>master_card</option>
                                    <option>american_express</option>
                                    <option>union_pay</option>
                                    <option>jcb</option>
                                </select>

                                <MDBBtn type="submit" >Send</MDBBtn>
                                <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn></div>

                            </form>
                                    
                                </div>
                                :
                                <div>
                                    {this.state.btnDisplay ?
                                        <div>
                                            <form onSubmit={this.handleSubmit2}>
                                                <MDBContainer>
                                                    <MDBListGroup style={{ width: "22rem" }}>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">Card id</label>
                                                        <MDBListGroupItem >{this.state.id}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">last_4</label>
                                                        <MDBListGroupItem >{this.state.last_4}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">brand</label>
                                                        <MDBListGroupItem  >{this.state.brand}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">expired at</label>
                                                        <MDBListGroupItem href="#" >{this.state.expired_at}</MDBListGroupItem>
                                                        <MDBBtn type="submit" >Afficher une carte</MDBBtn>
                                                        <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
                                                    </MDBListGroup>

                                                </MDBContainer>

                                            </form>

                                        </div>

                                        :
                                        <div>
                                            {this.state.btnDelete ?

                                                <div>
                                                    <form onSubmit={this.handleSubmitDelete}>
                                                        <label for="id">card-id to delete :</label>
                                                        <input type="text" id="id" name="id" onChange={this.handleChangeId}></input>
                                                        <MDBBtn type="submit">Delete</MDBBtn>
                                                        <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
                                                    </form>
                                                </div>
                                                :
                                                console.log("ok")
                                            }
                                        </div>} </div>} </div>
                    };
                    {this.state.checkConnection ? <Redirect to='/nav_Bar' /> : console.log("fail")}
                </div>
            </div>
        );
    }
}
export default Cards;
