import React, { Component } from 'react';
import { MDBBtn } from 'mdbreact';
import { MDBListGroup, MDBListGroupItem, MDBContainer } from "mdbreact";
import { BrowserRouter, Route, Link, Redirect } from 'react-router-dom';
import Nav_bar from './Nav_bar/nav_bar';


class Profiles extends Component {
    constructor(props) {
        console.log(localStorage.getItem('access_token'))
        super(props);
        this.state = {
            id: null,
            first_name: null,
            last_name: null,
            email: null,
            password: null,
            is_admin: null,
            btnCreate: false,
            btnDisplay: false,

            checkConnection: false,
            listCards: [],
            index: 2
        };
        this.handleBtnCreate = this.handleBtnCreate.bind(this);
        this.handleBtnDisplay = this.handleBtnDisplay.bind(this);
        this.handleBtnDisplayAll = this.handleBtnDisplayAll.bind(this);
        this.handleBtnDelete = this.handleBtnDelete.bind(this);
        this.handleBtnCancel = this.handleBtnCancel.bind(this);
        this.handleChangeFName = this.handleChangeFName.bind(this);
        this.handleChangeLName = this.handleChangeLName.bind(this);
        this.handleChangePwd = this.handleChangePwd.bind(this);
        this.handleChangeEmail = this.handleChangeEmail.bind(this); 
        this.handleChangeAdmin = this.handleChangeAdmin.bind(this);
        this.handleChangeId = this.handleChangeId.bind(this);

    }

    

    handleChangeAdmin(event) {
        this.setState({
          is_admin: event.target.value
        });
    }

    handleChangePwd(event) {
        this.setState({
            password: event.target.value
        });
    }

    handleChangeEmail(event) {
        this.setState({
            email: event.target.value
        });
    }
    handleChangeFName(event) {
        this.setState({
            first_name: event.target.value
        });
    }

    handleChangeLName(event) {
        this.setState({
            last_name: event.target.value
        });
    }

    handleChangeId(event) {
        this.setState({
            id: event.target.value
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        let result = fetch("http://localhost:8000/v1/users", {
            method: 'POST',
            body: JSON.stringify({
                "first_name": this.state.first_name,
                "last_name": this.state.last_name,
                "email": this.state.email,
                "password": this.state.password,
                "is_admin":this.state.is_admin
            }),
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': localStorage.getItem('access_token')
            }
        })
            .then(response => {
                response.json()
                    .then(value => {
                        if (response.status === 200) {
                            let access_token = response.access_token;
                            console.log(access_token);
                            this.setState({
                                checkConnection: true,
                            })
                        }
                    });
            });
    }

    handleSubmitDelete = (e) => {
        e.preventDefault();
        console.log("OKKKKKKKKKKKKKK")
        let url = "http://localhost:8000/v1/users/" + this.state.id
        let result = fetch(url, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': localStorage.getItem('access_token')
            }
        })
            .then(response => {

            });
    }


    handleBtnCreate() {
        this.setState({
            btnCreate: true
        })
    }

    handleBtnDisplay = (e) => {

        this.setState({
            btnDisplay: true
        })
    }
    handleBtnDisplayAll = (e) => {

        this.setState({
            btnDisplay: true
        })
    }

    handleBtnCancel = (e) => {
        console.log("dans cancel")
        this.setState({
            checkConnection: true,

        })
    }
    handleBtnDelete = (e) => {

        this.setState({
            btnDelete: true
        })
    }

    handleSubmit2 = (e) => {
        e.preventDefault();
        console.log("okkkkkkkkkkkk")
        let result = fetch("http://localhost:8000/v1/users", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': localStorage.getItem('access_token')
            }
        })
            .then(response => {
                response.json()
                    .then(value => {

                        if (response.status === 200) {
                            this.setState({
                                //   checkConnection: true,
                                id: value[0].id,
                                first_name: value[0].first_name,
                                last_name: value[0].last_name,
                                email: value[0].email,
                                password: value[0].password,
                                is_admin: value[0].is_admin


                            })
                        }
                    });
            });
    }


    render() {
        return (

            <div>

                <div>
                    <Nav_bar />
                    <MDBBtn onClick={this.handleBtnDisplay}>Display profiles</MDBBtn>
                    <MDBBtn onClick={this.handleBtnDisplayAll}>Display all profiles </MDBBtn>
                    <MDBBtn onClick={this.handleBtnDelete}>Delete profiles</MDBBtn>
                    <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
                    {this.state.btnCreate ?
                        <div>

                            <form onSubmit={this.handleSubmit}><div class="form-group col-md-4">
                                <label for="first_name">first_name :</label>
                                <input type="text" id="first_name" name="first_name" onChange={this.handleChangeFName} class="form-control"></input>
                                <label for="last_name">last_name:</label>
                                <input type="text" id="last_name" name="last_name" onChange={this.handleChangeLName} class="form-control"></input>
                                <label for="email">email:</label>
                                <input type="text" id="email" name="email" onChange={this.handleChangeEmail} class="form-control"></input>
                                <label for="password">password:</label>
                                <input type="text" id="password" name="password" onChange={this.handleChangePwd} class="form-control"></input>
                                
                                

                                <MDBBtn type="submit" >Send</MDBBtn>
                                <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn></div>

                            </form>
                        </div>
                        :
                        <div>
                            {this.state.btnDisplayAll ?
                                <div>
                                    <MDBContainer>
                                                    <MDBListGroup style={{ width: "22rem" }}>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">Profile id</label>
                                                        <MDBListGroupItem >{this.state.id}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">first_name</label>
                                                        <MDBListGroupItem >{this.state.first_name}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">last_name</label>
                                                        <MDBListGroupItem  >{this.state.last_name}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">email</label>
                                                        <MDBListGroupItem href="#" >{this.state.email}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">is_admin</label>
                                                        <MDBListGroupItem href="#" >{this.state.is_admin}</MDBListGroupItem>
                                                        <MDBBtn type="submit" >Afficher un profil </MDBBtn>
                                                        <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
                                                    </MDBListGroup>

                                                </MDBContainer>
                                    
                                </div>
                                :
                                <div>
                                    {this.state.btnDisplay ?
                                        <div>
                                            <form onSubmit={this.handleSubmit2}>
                                                <MDBContainer>
                                                    <MDBListGroup style={{ width: "22rem" }}>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">Profile id</label>
                                                        <MDBListGroupItem >{this.state.id}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">first_name</label>
                                                        <MDBListGroupItem >{this.state.first_name}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">last_name</label>
                                                        <MDBListGroupItem  >{this.state.last_name}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">email</label>
                                                        <MDBListGroupItem href="#" >{this.state.email}</MDBListGroupItem>
                                                        <label htmlFor="defaultFormContactNameEx" className="grey-text">is_admin</label>
                                                        <MDBListGroupItem href="#" >{this.state.is_admin}</MDBListGroupItem>
                                                        <MDBBtn type="submit" >Afficher un profil </MDBBtn>
                                                        <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
                                                    </MDBListGroup>

                                                </MDBContainer>

                                            </form>

                                        </div>

                                        :
                                        <div>
                                            {this.state.btnDelete ?

                                                <div>
                                                    <form onSubmit={this.handleSubmitDelete}>
                                                        <label for="id">profiles-id to delete :</label>
                                                        <input type="text" id="id" name="id" onChange={this.handleChangeId}></input>
                                                        <MDBBtn type="submit">Delete</MDBBtn>
                                                        <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
                                                    </form>
                                                </div>
                                                :
                                                console.log("ok")
                                            }
                                        </div>} </div>} </div>
                    };
                    {this.state.checkConnection ? <Redirect to='/nav_Bar' /> : console.log("fail")}
                </div>
            </div>
        );
    }
}
export default Profiles;
