import React, { Component } from "react";
import { MDBBtn } from "mdbreact";
import { MDBListGroup, MDBListGroupItem, MDBContainer } from "mdbreact";
import { BrowserRouter, Route, Link, Redirect } from "react-router-dom";
import Nav_bar from "./Nav_bar/nav_bar";

class Cards extends Component {
  constructor(props) {
    console.log(localStorage.getItem("access_token"));
    super(props);
    this.state = {
      id: null,
      GPA: null,
      coffee: null,
      weight: null,
      btnCreate: false,
      btnDisplay: false,
      checkConnection: false,
      listCards: [],
      index: 2,
    };

    this.handleBtnUpdate = this.handleBtnUpdate.bind(this);
    this.handleBtnDisplay = this.handleBtnDisplay.bind(this);
    this.handleBtnDisplayAll = this.handleBtnDisplayAll.bind(this);
    this.handleBtnDelete = this.handleBtnDelete.bind(this);
    this.handleBtnCancel = this.handleBtnCancel.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleChangePwd = this.handleChangePwd.bind(this);
    this.handleChangeExpired = this.handleChangeExpired.bind(this);
    this.handleChangeId = this.handleChangeId.bind(this);
  }

  handleChange(event) {
    this.setState({
      brand: event.target.value,
    });
  }

  handleChangePwd(event) {
    this.setState({
      last_4: event.target.value,
    });
  }

  handleChangeExpired(event) {
    this.setState({
      expired_at: event.target.value,
    });
  }

  handleChangeId(event) {
    this.setState({
      id: event.target.value,
    });
  }
  handleSubmit = (e) => {
    e.preventDefault();
    let result = fetch("http://localhost:8000/v1/cards", {
      method: "POST",
      body: JSON.stringify({
        last_4: this.state.last_4,
        brand: this.state.brand,
        expired_at: this.state.expired_at,
      }),
      headers: {
        "Content-Type": "application/json",
        "x-auth-token": localStorage.getItem("access_token"),
      },
    }).then((response) => {
      response.json().then((value) => {
        if (response.status === 200) {
          let access_token = response.access_token;
          console.log(access_token);
          this.setState({
            checkConnection: true,
          });
        }
      });
    });
  };

  handleSubmit2 = (e) => {
    e.preventDefault();
    console.log("okkkkkkkkkkkk");
    let result = fetch("http://localhost:8000/v1/cards", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "x-auth-token": localStorage.getItem("access_token"),
      },
    }).then((response) => {
      response.json().then((value) => {
        if (response.status === 200) {
          this.setState({
            //   checkConnection: true,
            id: value[0].id,
            last_4: value[0].last_4,
            brand: value[0].brand,
            expired_at: value[0].expired_at,
          });
        }
      });
    });
  };

  render() {
    return (
      <div>
        <div>
          <Nav_bar />
          <MDBBtn onClick={this.handleBtnCreate}>Create card</MDBBtn>
          <div>
            <form onSubmit={this.handleSubmit}>
              <div class="form-group col-md-4">
                <label for="last_4">last_4 :</label>
                <input
                  type="text"
                  id="last_4"
                  name="last_4"
                  onChange={this.handleChangePwd}
                  class="form-control"
                ></input>
                <label for="expired_at">expired_at:</label>

                <input
                  type="text"
                  id="expired_at"
                  name="expired_at"
                  onChange={this.handleChangeExpired}
                  class="form-control"
                ></input>

                <label for="inputState">Brand</label>
                <select
                  id="brand"
                  name="brand"
                  onChange={this.handleChange}
                  class="form-control"
                >
                  <option brand>Choose...</option>
                  <option>visa</option>
                  <option>master_card</option>
                  <option>american_express</option>
                  <option>union_pay</option>
                  <option>jcb</option>
                </select>

                <MDBBtn type="submit">Send</MDBBtn>
                <MDBBtn onClick={this.handleBtnCancel}>Cancel</MDBBtn>
              </div>
            </form>
          </div>
          {this.state.checkConnection ? (
            <Redirect to="/nav_Bar" />
          ) : (
            console.log("fail")
          )}
        </div>
      </div>
    );
  }
}
export default Cards;
