import React, { Component } from "react";
import { MDBBtn } from "mdbreact";
import { MDBListGroup, MDBListGroupItem, MDBContainer } from "mdbreact";
import { BrowserRouter, Route, Link, Redirect } from "react-router-dom";
import Nav_bar from "./Nav_bar/nav_bar";

class Cards extends Component {
  constructor(props) {
    console.log(localStorage.getItem("access_token"));
    super(props);
    this.state = {
      id: null,
      GPA: null,
      coffee: null,
      weight: null,
      Gender: null,
      breakfast: null,
      fruit_day: null,
      veggies_day: null,
      eating_out: null,
      soup: null,
      fries: null,
      btnCreate: false,
      btnDisplay: false,
      checkConnection: false,
      listCards: [],
      index: 2,
    };

   

    this.handleChange = this.handleChange.bind(this);
    this.handleChangeGPA = this.handleChangeGPA.bind(this);
    this.handleChangeCoffee = this.handleChangeCoffee.bind(this);
    this.handleChangeWeight = this.handleChangeWeight.bind(this);
    this.handleChangeGender= this.handleChangeGender.bind(this);
    this.handleChangeBreakfast= this.handleChangeBreakfast.bind(this);
    this.handleChangeFruit_day= this.handleChangeFruit_day.bind(this);
    this.handleChangeVeggies_day= this.handleChangeVeggies_day.bind(this);
    this.handleChangeEating_out= this.handleChangeEating_out.bind(this);
    this.handleChangeSoup= this.handleChangeSoup.bind(this);
    this.handleChangeFries= this.handleChangeFries.bind(this);

  }
  handleChangeGender(event) {
    this.setState({
      Gender: event.target.value,
    });
  }
  handleChangeFries(event) {
    this.setState({
      fries: event.target.value,
    });
  }
  handleChangeVeggies_day(event) {
    this.setState({
      veggies_day: event.target.value,
    });
  }
  handleChangeEating_out(event) {
    this.setState({
      eating_out: event.target.value,
    });
  }
  handleChangeSoup(event) {
    this.setState({
      soup: event.target.value,
    });
  }
  handleChangeFruit_day(event) {
    this.setState({
      fruit_day: event.target.value,
    });
  }
  handleChange(event) {
    this.setState({
      coffee: event.target.value,
    });
  }
  handleChangeBreakfast(event) {
    this.setState({
      breakfast: event.target.value,
    });
  }

  handleChangeGPA(event) {
    this.setState({
      GPA: event.target.value,
    });
  }

  handleChangeCoffee(event) {
    this.setState({
      coffee: event.target.value,
    });
  }

  handleChangeWeight(event) {
    this.setState({
      Weight: event.target.value,
    });
  }
  handleSubmit = (e) => {
    e.preventDefault();
     fetch("http://6f50ceb6.ngrok.io/result",{ //+ this.coffee + "/"+ this.Gender+"/"+ this.breakfast+ "/"+ this.fruit_day+ "/"+ this.veggies_day+ "/"+ this.eating_out+ "/"+ this.soup+ "/"+ this.fries, {
      method: "GET",
    }).then((response) => {
      response.json().then((value) => {
        
          console.log("coffee"+this.coffee);
          this.setState({
            GPA:value[1],
          });
       
      });
    });
  };

  handleSubmit2 = (e) => {
    e.preventDefault();
    console.log("okkkkkkkkkkkk");
    let result = fetch("http://localhost:8000/v1/cards", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "x-auth-token": localStorage.getItem("access_token"),
      },
    }).then((response) => {
      response.json().then((value) => {
        if (response.status === 200) {
          this.setState({
            //   checkConnection: true,
            id: value[0].id,
            last_4: value[0].last_4,
            brand: value[0].brand,
            expired_at: value[0].expired_at,
          });
        }
      });
    });
  };

  render() {
    return (
      <div>
        <div>
          <div>
            <form onSubmit={this.handleSubmit}>
              <div class="form-group col-md-4">
              <label for="inputState">Do you drink coffee</label>
                <select
                  id="brand"
                  name="brand"
                  onChange={this.handleChange}
                  class="form-control"
                >
                  <option brand>Choose...</option>
                  <option>Oui</option>
                  <option>Non</option>
                </select>

                <label for="weight">Enter your weight</label>
                <input type="text" id="weight" name="weight" onChange={this.handleChangeWeight} class="form-control"></input>

                <label for="weight">Enter veggie day</label>
                <input type="veggies_day" id="veggies_day" name="weight" onChange={this.handleChangeVeggies_day} class="form-control"></input>

                <label for="eating_out">Enter eating out</label>
                <input type="text" id="eating_out" name="eating_out" onChange={this.handleChangeEating_out} class="form-control"></input>
               
                <label for="soup">Enter soup</label>
                <input type="text" id="soup" name="soup" onChange={this.handleChangeSoup} class="form-control"></input>

                <label for="fries">Enter eating out</label>
                <input type="text" id="fries" name="fries" onChange={this.handleChangeFries} class="form-control"></input>

                <label for="fruit_day">Enter how many fruit_day you eat by day</label>
                <input type="text" id="fruit_day" name="fruit_day" onChange={this.handleChangeFruit_day} class="form-control"></input>

                <label for="inputState">Breakfast</label>
                <select
                  id="brand"
                  name="brand"
                  onChange={this.handleChange}
                  class="form-control"
                >
                  <option brand>Choose...</option>
                  <option>yes</option>
                  <option>No</option>
                </select>

                

                <MDBBtn type="submit">Accéder à mon bilan</MDBBtn>
              </div>
              {this.handleSubmit ? <div>

                {this.GPA ===1 ? <div> You have great chance to have a lot of reussite at school. Continue like that

              </div> : <div> Ohhh you are alimenettion is not very good you have bad chance to have success</div>}</div>
  
  :
  console.log("ok")}
            </form>
          </div>
        </div>
      </div>
    );
  }
}
export default Cards;
